import React from "react";


const userInfoReducer = (state, action) => {
  switch (action.type) {
    case 'setlastname':
      return { lastname: action.payload };
      break;
    case 'setusername':
      return action.payload;
  }
};

function Heading() {
  const [reducer, dispatch] = React.useReducer(userInfoReducer, {
    name: "John",
    lastname: "Doe"
  });

  return (
    <React.Fragment>
      <h3>
        {reducer.name} {reducer.lastname}
      </h3>
      <EditUsername name={reducer.name} dispatch={dispatch} />
      <input
        value={reducer.lastname}
        onChange={e =>
          dispatch({
            type: "setlastname",
            payload: e.target.value
          })
        }
      />
    </React.Fragment>
  );
};

const EditUsername = React.memo(props => {
  console.log(
    "Hey I'm only rerendered when name gets updated, check React.memo"
  );

  return (
    <input
      value={props.name}
      onChange={e =>
        props.dispatch({
          type: "setusername",
          payload: e.target.value
        })
      }
    />
  );
});
export default Heading;
